import React, { Component } from 'react';
import { FiniteStateMachine } from './FiniteStateMachine';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      currentFloor: 1,
      currentState: 'idle'
    };
    this.fsm = new FiniteStateMachine();
    this.fsm.addState('idle');
    this.fsm.addState('moving');
    this.fsm.addState('doorOpening');
    this.fsm.addState('doorOpen');
    this.fsm.addState('doorClosing');
    this.fsm.addTransition('idle', 'moving', 'CALL_ELEVATOR');
    this.fsm.addTransition('moving', 'doorOpening', 'ARRIVED_AT_FLOOR');
    this.fsm.addTransition('doorOpening', 'doorOpen', 'DOOR_OPENED');
    this.fsm.addTransition('doorOpen', 'doorClosing', 'DOOR_CLOSE');
    this.fsm.addTransition('doorClosing', 'idle', 'DOOR_CLOSED');
  }

  moveElevatorToFloor = (floor) => {
    if (floor > this.state.currentFloor) {
      this.setState({currentFloor: this.state.currentFloor + 1},setTimeout(()=>{this.moveElevatorToFloor(floor)}, 1000))
    }
  }

  callElevator = (floor) => {
    this.fsm.handleEvent('CALL_ELEVATOR');
    this.setState({ currentState: this.fsm.currentState });
    if (floor !== this.state.currentFloor) {
      moveElevatorToFloor(floor);
    }
  };

  arriveAtFloor = (floor) => {
    const { currentState } = this.state;
    if (currentState === 'moving') {
      this.setState({ currentFloor: floor });
      this.fsm.handleEvent('ARRIVED_AT_FLOOR');
      this.setState({ currentState: this.fsm.currentState });
    }
  };

  openDoor = () => {
    this.fsm.handleEvent('DOOR_OPENED');
    this.setState({ currentState: this.fsm.currentState });
    setTimeout(() => {
      this.fsm.handleEvent('DOOR_CLOSE');
      this.setState({ currentState: this.fsm.currentState });
    }, 3000); // Simulating 3 seconds door open duration
  };

  closeDoor = () => {
    this.fsm.handleEvent('DOOR_CLOSED');
    this.setState({ currentState: this.fsm.currentState });
  };

  render() {
    const { currentFloor, currentState } = this.state;

    return (
      <div>
        <h1>Elevator Simulation</h1>
        <p>Current Floor: {currentFloor}</p>
        <p>Elevator State: {currentState}</p>
        {currentState === 'idle' && (
          <button onClick={this.callElevator}>Call Elevator</button>
        )}
        {currentState === 'moving' && currentFloor !== 3 && (
          <button onClick={()=>{this.arriveAtFloor(3)}}>Simulate Arrival at Floor 3</button>
        )}
         {currentState === 'moving' && currentFloor === 3 && (
          <button onClick={()=>{this.arriveAtFloor(1)}}>Simulate Arrival at Floor 1 </button>
        )}
        {currentState === 'doorOpening' && (
          <button onClick={this.openDoor}>Opening Door...</button>
        )}
        {currentState === 'doorOpen' && (
          <p>Door Open</p>
        )}
        {currentState === 'doorClosing' && (
          <button onClick={this.closeDoor}>Closing Door...</button>
        )}
      </div>
    );
  }
}

export default App;
