import React, { useState, useEffect } from 'react';
import { FiniteStateMachine } from './FiniteStateMachine';

const App = () => {
  const [currentFloor, setCurrentFloor] = useState(1);
  const [currentState, setCurrentState] = useState('idle');
  const [shouldHandleEvent, setShouldHandleEvent] = useState(false);

  const fsm = new FiniteStateMachine();
  fsm.addState('idle');
  fsm.addState('moving');
  fsm.addState('doorOpening');
  fsm.addState('doorClosing');
  fsm.addTransition('idle', 'moving', 'CALL_ELEVATOR');
  fsm.addTransition('moving', 'doorOpening', 'ARRIVED_AT_FLOOR');
  fsm.addTransition('doorOpening', 'idle', 'DOOR_OPENED');
  fsm.addTransition('idle', 'doorClosing', 'DOOR_CLOSE');
  fsm.addTransition('doorClosing', 'moving', 'DOOR_CLOSED');

  const callElevator = () => {
    fsm.handleEvent('CALL_ELEVATOR');
    setCurrentState(fsm.currentState);
  };

  const arriveAtFloor = () => {
    setShouldHandleEvent(true);
  };

  useEffect(() => {
    if (shouldHandleEvent && currentState === 'moving') {
      setCurrentFloor(5);
      fsm.handleEvent('ARRIVED_AT_FLOOR');
      setCurrentState(fsm.currentState);
      setShouldHandleEvent(false);
    }
  }, [shouldHandleEvent, currentState, fsm]);

  const openDoor = () => {
    fsm.handleEvent('DOOR_OPENED');
    setCurrentState(fsm.currentState);
  };

  const closeDoor = () => {
    fsm.handleEvent('DOOR_CLOSED');
    setCurrentState(fsm.currentState);
  };

  return (
    <div>
      <h1>Elevator Simulation</h1>
      <p>Current Floor: {currentFloor}</p>
      <p>Elevator State: {currentState}</p>
      {currentState === 'idle' && (
        <button onClick={callElevator}>Call Elevator</button>
      )}
      {currentState === 'moving' && (
        <button onClick={arriveAtFloor}>Simulate Arrival at Floor 5</button>
      )}
      {currentState === 'doorOpening' && (
        <button onClick={openDoor} disabled>Opening Door...</button>
      )}
      {currentState === 'doorClosing' && (
        <button onClick={closeDoor} disabled>Closing Door...</button>
      )}
    </div>
  );
};

export default App;
